package com.pay.one.alipay;import java.util.Map;public class AliPayResult {    public int resultCode;    public String result;    public String memo;    public AliPayResult(Map<String, String> rawResult) {        if (rawResult == null) {            return;        }        String status = rawResult.get("resultStatus");        try {            if (status != null) {                resultCode = Integer.parseInt(status);            }        } catch (Exception e) {            e.printStackTrace();        }        result = rawResult.get("result");        memo = rawResult.get("memo");    }}